order_count = order_to_twitter.group_by {|handle| handle.to_s.length } # Affiche le nombre de caractères en num => hash 

order_count.keys.map {|n| puts "#{order_count[n].count} il y à tant de handles dans #{n}"}  # keys recup les clefs => map boucle 



